package string;

public class String1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="hello world";
		String s1= new String("Welcome to 3RI");
		char s2[]= {'c','a','t'};
		String s3=new String(s2);
		System.out.println("The first given string is: "+s);
		System.out.println("The second given string is: "+s1);
		System.out.println("The third given string is: "+s3);
	}

}
